// Circle
	if ( ! function_exists( 'skin_draw_circle' ) ) :
		function skin_draw_circle( $cx = 25, $cy = 25, $r = 25, $stroke_w = 1 ) {
			$d = 2*$r;
			
			$circle = sprintf(
				'<svg class="svg-circle" width="%1$s" height="%1$s"><circle class="svg-stroke" cx="%2$s" cy="%3$s" r="%4$s" fill="none" stroke-width="%5$s" /></svg>',
				absint( $d ),
				esc_attr( $cx ),
				esc_attr( $cy ),
				absint( $r ),
				absint( $stroke_w )
			);
			
			return $circle;
		}
	endif;
	
